import { Component, OnInit } from '@angular/core';
import { AbstractControl, FormArray, FormBuilder, FormGroup, Validators } from '@angular/forms';

// BGM by OtoLogic (CC BY 4.0) https://otologic.jp : 「時計 アラーム03」を加工 https://otologic.jp/free/se/clock01.html
// 「ピピピピ」1回
//const base64 = '';
// 「ピピピピ・ピピピピ」2回
const base64 = '';

/** プレビュー表示のデフォルト値 */
const defaultFaceValues = {
  timerId: null,
  currentIndex: null,
  memo: '',
  minutes: '00',
  seconds: '00'
} as const;

/** App Component */
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent implements OnInit {
  /** 状態定義 */
  public states = {
    /** タイマー停止中 */
    stopping: 'stopping',
    /** タイマー実行中 */
    starting: 'starting'
  } as const;  // 再帰的に readonly にする https://typescriptbook.jp/reference/values-types-variables/const-assertion
  
  /** 現在の状態 https://weseek.co.jp/tech/1609/ */
  public currentState: typeof this.states[keyof typeof this.states] = this.states.stopping;
  
  /** タイマー情報入力フォーム */
  public timersForm!: FormGroup;
  
  /** Getter */
  public get timers(): FormArray {
    return this.timersForm.get('timers') as FormArray;
  }
  
  /** プレビュー表示 */
  public face: {
    /** `setInterval()` のタイマー ID を控える */
    timerId: number | null;
    /** 実行中のタイマーの Index */
    currentIndex: number | null;
    /** メモ */
    memo: string;
    /** 分表示 (ゼロパディングアリ) */
    minutes: string;
    /** 秒表示 (ゼロパディングアリ) */
    seconds: string;
  } = Object.assign({}, defaultFaceValues);
  
  /** LocalStorage にデータがあるかどうか */
  public hasLocalStorageValue: boolean = false;
  
  /** LocalStorage のキー */
  private readonly localStorageKey: 'timersForm' = 'timersForm';
  
  /** アラーム音 */
  private audio: HTMLAudioElement = new Audio('data:audio/mp3;base64,' + base64);
  
  /** Constructor */
  constructor(private formBuilder: FormBuilder) {}
  
  /** 初期処理 */
  public ngOnInit(): void {
    // LocalStorage に値があれば「Load」ボタンを使えるようにしておく・自動復元はしない
    this.checkLocalStorageValue();
    
    // フォームをゼロから組み立てる
    this.timersForm = this.formBuilder.group({
      timers: this.formBuilder.array([])
    });
    this.face = Object.assign({}, defaultFaceValues);
    
    // クエリパラメータがなければダミーの1行を入れて終了する
    const urlSearchParams = [...new URLSearchParams(window.location.search)];
    if(!urlSearchParams.length) return this.insertRow();
    
    // クエリパラメータの値を整理する
    const values: Array<{ seconds: number; memo: string; }> = [];
    urlSearchParams.forEach(([ key, value ]) => {
      const type = key.slice(0, 1);
      const index = Number(key.slice(1));
      // 異常値になるクエリパラメータは無視する
      if(!['s', 'm'].includes(type) || Number.isNaN(index)) return console.log('Invalid Search Param', key, value);
      // オブジェクトがなければひとまず空値で作る
      if(values[index] == null) values[index] = { seconds: 0, memo: '' };
      // 値を設定する
      if(type === 's') values[index].seconds = Number(value);
      if(type === 'm') values[index].memo = value;
    });
    // 正常な値がなければダミーの1行を入れて終了する
    if(!values.length) return this.insertRow();
    
    // Empty な行が混じっていると `forEach()` で回らないので空値を入れる https://ginpen.com/2018/12/22/empty-slots/
    for(let i = 0; i < values.length; i++) {
      if(!(i in values)) values[i] = { seconds: 0, memo: '' };  // 異常値のゼロを入れておく
    }
    // フォーム行を追加する
    values.forEach((row) => {
      this.insertRow(undefined, row.seconds, row.memo);
    });
  }
  
  /**
   * 秒数の入力時「1.00」のような小数が Validators をすり抜けて許容されてしまうので Blur 時に強制的に修正する
   * 
   * @param timerFormGroup 指定行の FormGroup
   */
  public onBlurFormatSeconds(timerFormGroup: AbstractControl) {
    if(timerFormGroup.valid) timerFormGroup.get('seconds')!.setValue(Number(timerFormGroup.value.seconds));
  }
  
  /**
   * 行追加する
   * 
   * @param index 指定の Index 行の下に追加する・未指定の場合は最終行に追加する
   * @param seconds 値として設定したい秒数・未指定の場合はダミー値を入れる
   * @param memo 値として設定したいメモ・未指定の場合はダミー値を入れる
   */
  public insertRow(index?: number, seconds?: number, memo?: string): void {
    const targetIndex  = index == null ? this.timers.length : index + 1;
    const secondsValue = seconds ?? String(5 * (this.timers.length + 1));
    const memoValue    = memo    ?? `Memo ${this.timers.length + 1}`;
    this.timers.insert(targetIndex, this.formBuilder.group({
      seconds: [secondsValue, [Validators.required, Validators.min(1), Validators.max(3600), Validators.pattern('[0-9]*')]],
      memo   : [memoValue]
    }));
    this.timersForm.markAsDirty();
  }
  
  /**
   * 行削除する
   * 
   * @param index 指定の Index 行を削除する
   */
  public removeRow(index: number): void {
    this.timers.removeAt(index);
    this.timersForm.markAsDirty();
  }
  
  /**
   * 指定行を1行上に移動する
   * 
   * @param index 指定行・0番目の Index は使用しないこと
   */
  public moveUp(index: number): void {
    const tempRow = this.timers.at(index);
    this.timers.removeAt(index);
    this.timers.insert(index - 1, tempRow);
    this.timersForm.markAsDirty();
  }
  
  /**
   * 指定行を1行下に移動する
   * 
   * @param index 指定行・最終行の Index は指定しないこと
   */
  public moveDown(index: number): void {
    const tempRow = this.timers.at(index + 1);
    this.timers.removeAt(index + 1);
    this.timers.insert(index, tempRow);
    this.timersForm.markAsDirty();
  }
  
  /** タイマーを開始・途中から再開する */
  public start(): void {
    // フォームが一時停止後に編集された場合は最初から実行し直す
    if(this.timersForm.dirty) this.face.currentIndex = null;
    // Disable にすると Pristine 状態になる
    this.timers.disable();
    this.currentState = this.states.starting;
    // `setInterval()` は最初の1秒動作しないので先に初回実行しておく・再開時は1秒待たせるため実行しない
    if(this.face.currentIndex == null) this.execTimer();
    this.face.timerId = window.setInterval(this.execTimer.bind(this), 1000);
    this.formToUrlSearchParams();  // クエリパラメータにも設定する
  }
  
  /** タイマーを一時停止する */
  public stop(): void {
    this.timers.enable();
    this.currentState = this.states.stopping;
    window.clearInterval(this.face.timerId!);
    // タイマー停止後に編集した場合が分かるように停止直後を初期状態とみなす
    this.timersForm.markAsPristine();
  }
  
  /** タイマーを最初から開始し直す */
  public restart(): void {
    this.face.currentIndex = null;
    this.start();
  }
  
  /** 前の行のタイマーに戻る */
  public back(): void {
    window.clearInterval(this.face.timerId!);
    this.showAtFirstTime(this.face.currentIndex! - 1);
    this.face.timerId = window.setInterval(this.execTimer.bind(this), 1000);
  }
  
  /** 次の行のタイマーに進む */
  public next(): void {
    window.clearInterval(this.face.timerId!);
    this.showAtFirstTime(this.face.currentIndex! + 1);
    this.face.timerId = window.setInterval(this.execTimer.bind(this), 1000);
  }
  
  /** フォームを LocalStorage に保存する */
  public save(): void {
    window.localStorage.setItem(this.localStorageKey, JSON.stringify(this.timers.value));
    this.hasLocalStorageValue = true;
    this.formToUrlSearchParams();  // クエリパラメータにも設定する
  }
  
  /** LocalStorage に保存したフォームを復元する */
  public load(): void {
    try {
      const rawLocalStorageValue = window.localStorage.getItem(this.localStorageKey);
      if(rawLocalStorageValue == null || rawLocalStorageValue.trim() === '') throw new Error('LocalStorage Value Is Empty');
      const localStorageValues = JSON.parse(rawLocalStorageValue);
      if(!Array.isArray(localStorageValues)) throw new Error('LocalStorage Value Is Invalid Type');
      // フォームをゼロから組み立てる
      this.timersForm = this.formBuilder.group({
        timers: this.formBuilder.array([])
      });
      localStorageValues.forEach((row) => {
        this.insertRow(undefined, row.seconds, row.memo);
      });
      this.face = Object.assign({}, defaultFaceValues);
      this.formToUrlSearchParams();  // クエリパラメータにも設定する
    }
    catch(error) {
      console.error('Failed To Load LocalStorage Value', error);
    }
  }
  
  /** リセット */
  public reset(): void {
    this.timersForm = this.formBuilder.group({
      timers: this.formBuilder.array([])
    });
    this.insertRow();  // ダミーの1行を入れておく
    this.face = Object.assign({}, defaultFaceValues);
    // クエリパラメータを空にする
    const url = new URL(window.location.href);
    url.search = '';
    window.history.replaceState(null, '', url.href);
  }
  
  /** LocalStorage に値があるかどうかチェックする */
  private checkLocalStorageValue(): void {
    try {
      const localStorageValue = window.localStorage.getItem(this.localStorageKey);
      // 空値の場合
      if(localStorageValue == null || localStorageValue.trim() === '') {
        this.hasLocalStorageValue = false;
        return;
      }
      // JSON パースしてエラーが出ないかどうかチェックする
      JSON.parse(localStorageValue);
      this.hasLocalStorageValue = true;
    }
    catch(error) {  // JSON パースに失敗したら値がない扱いにする
      console.warn('Failed To Check LocalStorage Value', error);
      window.localStorage.removeItem(this.localStorageKey);
      this.hasLocalStorageValue = false;
    }
  }
  
  /** タイマーごとの処理 */
  private execTimer(): void {
    // 初回
    if(this.face.currentIndex == null) return this.showAtFirstTime(0);
    const beforeMinutes = Number(this.face.minutes);
    const beforeSeconds = Number(this.face.seconds);
    // 現在の Index のカウントダウンが終了
    if(beforeMinutes === 0 && beforeSeconds === 0) {
      // アラーム音を鳴らす
      this.audio.play();
      // 最後のタイマーが終了した場合 : タイマーを停止する
      if(this.timers.controls[this.face.currentIndex + 1] == null) {
        this.face.currentIndex = null; // 次に開始した時に最初から実行できるようにリセットしておく
        return this.stop();
      }
      // 次のタイマーを開始する
      return this.showAtFirstTime(this.face.currentIndex + 1);
    }
    // 分数が減る瞬間
    if(beforeSeconds === 0) return this.updateTimeFace(beforeMinutes - 1, 59);
    // 秒のみのカウントダウン
    return this.updateTimeFace(beforeMinutes, beforeSeconds - 1);
  }
  
  /**
   * 分・秒の表示を更新する (ゼロパディングする)
   * 
   * @param minutes 分
   * @param seconds 秒
   */
  private updateTimeFace(minutes: number, seconds: number): void {
    this.face.minutes = `0${minutes}`.slice(-2);
    this.face.seconds = `0${seconds}`.slice(-2);
  }
  
  /**
   * 指定の Index のタイマーを最初にカウントダウンする際の処理
   * 
   * @param index カウントダウンを開始するタイマーの添字
   */
  private showAtFirstTime(index: number): void {
    this.face.currentIndex = index;
    const rawSeconds = Number(this.timers.controls[this.face.currentIndex!].get('seconds')!.value);
    const minutes = Math.floor(rawSeconds / 60);
    const seconds = Math.floor(rawSeconds % 60);
    this.updateTimeFace(minutes, seconds);
    this.face.memo = this.timers.controls[this.face.currentIndex!].get('memo')!.value;
  }
  
  /** `this.timers` の値を基にクエリパラメータを組み立て置換する */
  private formToUrlSearchParams(): void {
    const params = this.timers.value.reduce((params: any, row: { seconds: number; memo: string; }, index: number) => {
      params[`s${index}`] = row.seconds;
      if(row.memo != null && row.memo.trim() !== '') params[`m${index}`] = row.memo.trim();
      return params;
    }, {});
    const urlSearchParams = new URLSearchParams(params);
    const url = new URL(window.location.href);
    url.search = urlSearchParams.toString();
    window.history.replaceState(null, '', url.href);
  }
}
